import{_ as e,o as t,c as o,f as a}from"./app.2ca49c98.js";const l={},r=a('<h1 id="red-black-trees" tabindex="-1"><a class="header-anchor" href="#red-black-trees" aria-hidden="true">#</a> Red Black Trees</h1><p><code>Red-Black Trees</code> are binary search trees that are named after the way the nodes are coloured.</p><blockquote><p>The height of a red black tree is at most 2 * log(n+1).</p></blockquote><p>A red black tree must maintain the following colouring rules:</p><ol><li>every node must have a colour either red or black.</li><li>The root node must be black</li><li>If a node is red, its children must be black (note that this also implies that red nodes cannot have red parents)</li><li>Every path from root to a null node must have exactly the same number of black nodes</li></ol><div class="custom-container tip"><p class="custom-container-title">Note</p><p><code>null nodes</code> are also sometimes called <code>null leafs</code>. these are not really nodes.. they are the &quot;nodes&quot; that null pointers point to...</p></div>',6),s=[r];function c(n,d){return t(),o("div",null,s)}const h=e(l,[["render",c],["__file","RBT.html.vue"]]);export{h as default};
